import * as React from "react";
import styled from "styled-components";

interface Props {
  onClick: () => void;
  label: string;
}

const Btn = styled.button`
  height: 50px;
  padding-right: 20px;
  padding-left: 20px;
`;

const Button: React.SFC<Props> = props => {
  return <Btn onClick={props.onClick}>{props.label}</Btn>;
};

export default Button;
