import * as React from "react";
import styled from "styled-components";
import { Pony } from "types";

const CardWrapper = styled.div`
  height: 250px;
  width: 200px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  border-radius: 10px;
  box-shadow: 0px 0px 56px 0px rgba(60, 102, 110, 1);
  background-color: white;
`;

type Props = Pony;

const Card: React.SFC<Props> = props => {
  return (
    <CardWrapper>
      <a href="#">{props.name}</a>
      <a href="#">{props.color}</a>
      <a href="#">{props.kind}</a>
      <a href="#">{props.price + "$"}</a>
    </CardWrapper>
  );
};

export default Card;
