import * as React from "react";
import styled from "styled-components";
import { Pony } from "types";

const CardWrapper = styled.div`
  height: 250px;
  width: 200px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  border-radius: 10px;
  box-shadow: 0px 0px 56px 0px rgba(60, 102, 110, 1);
  background-color: white;
`;

interface Props extends Pony {
  // onSelect: () => void;
  imgUrl?: string;
}

// можно добавить конвертацию валюты и тип валюты

class Card extends React.Component<Props> {
  constructor(props: Props) {
    super(props);
    this.state = {
      open: false,
      selected: null
    };
  }
  public render() {
    return (
      <CardWrapper>
        <a href="#">{this.props.name}</a>
        <a href="#">{this.props.color}</a>
        <a href="#">{this.props.kind}</a>
        <a href="#">{this.props.price + "$"}</a>
      </CardWrapper>
    );
  }
}

export default Card;
