import { action } from "typesafe-actions";
import { CatalogTypes } from "store/catalog/types";

export const show = () => action(CatalogTypes.SHOW_FILTER);
export const close = () => action(CatalogTypes.CLOSE_FILTER);
export const updateFilterOpenLink = (link: string) =>
  action(CatalogTypes.CHANGE_FILTER_OPEN_LINK, link);
