import { Reducer } from "redux";
import { CatalogState, CatalogTypes } from "store/catalog/types";
import { ponies } from "./mock";

const initialState: CatalogState = {
  items: ponies,
  isShowFilter: true,
  filterOpenLink: "/static"
};

const catalogReducer: Reducer<CatalogState> = (
  state = initialState,
  action
): CatalogState => {
  switch (action.type) {
    case CatalogTypes.SHOW_FILTER: {
      return { ...state, isShowFilter: true };
    }
    case CatalogTypes.CLOSE_FILTER: {
      return { ...state, isShowFilter: false };
    }
    case CatalogTypes.CHANGE_FILTER_OPEN_LINK: {
      return { ...state, filterOpenLink: action.payload };
    }
    default:
      return state;
  }
};

export default catalogReducer;
