import { Reducer } from "redux";
import { FilterState, FilterTypes } from "store/filter/types";

const initialState: FilterState = {
  color: undefined,
  kind: undefined,
  isNew: undefined,
  priceRange: undefined
};
const filterReducer: Reducer<FilterState> = (
  state = initialState,
  action
): FilterState => {
  const payload: FilterState = action.payload;
  switch (action.type) {
    case FilterTypes.SET_FILTER: {
      return {
        color: payload.color,
        kind: payload.kind,
        isNew: payload.isNew,
        priceRange: payload.priceRange
      };
    }
    case FilterTypes.CLEAR_FILTER: {
      return {
        color: undefined,
        kind: undefined,
        isNew: undefined,
        priceRange: undefined
      };
    }
    default:
      return state;
  }
};

export default filterReducer;
