import { createStore, combineReducers, Store } from "redux";
import { CatalogState } from "store/catalog/types";
import { FilterState } from "store/filter/types";
import catalogReducer from "store/catalog/reducer";
import filterReducer from "store/filter/reducer";

export interface AppState {
  catalog: CatalogState;
  filter: FilterState;
}

const reducers = combineReducers<AppState>({
  catalog: catalogReducer,
  filter: filterReducer
});

const store: Store<AppState> = createStore(reducers);


export default store;
